// Components
import { VSnackbar } from 'vuetify/lib'

export default {
  name: 'BaseMaterialSnackbar',

  extends: VSnackbar,

  props: {
    dismissible: {
      type: Boolean,
      default: true,
    },
    type: {
      type: String,
      default: '',
    },
  },

  computed: {
    classes () {
      return {
        ...VSnackbar.options.computed.classes.call(this),
        'v-snackbar--material': true,
      }
    },
  },
}
